import React, { useState } from "react";
import { useToast } from "@chakra-ui/react";

import {
  Stack,
  HStack,
  VStack,
  Input,
  FormControl,
  FormLabel,
  InputGroup,
  InputRightElement,
  Button,
  useStatStyles,
} from "@chakra-ui/react";

const Signup = () => {
  const toast = useToast();
  const [load, setLoad] = useState(false);
  const [show, setShow] = useState(false);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [pass, setPass] = useState("");
  const [cpass, setCpass] = useState("");
  const [pic, setPic] = useState("");
  const handleClick = () => setShow(!show);

  const postPic = async (pic) => {
    setLoad(true);

    if (pic === undefined) {
      toast({
        title: "Upload Image",
        description: "Please upload a valid image for Profile",
        status: "warning",
        duration: 5000,
        isClosable: true,
        position: "bottom",
      });

      return;
    }

    if (pic.type === "image/jpeg" || pic.type === "iamge/png") {
      const data = new FormData();
      data.append("file", pic);
      data.append("upload_preset", "chat app");
      data.append("cloud_name", "dzkli6tdy");
      let res = await fetch(
        "https://res.cloudinary.com/v1_1/dzkli6tdy/image/upload",
        {
          method: "post",
          body: data,
        }
      );
      res = await res.json();
      setPic(res.url.toString());
      console.log(res.url.toString());
      setLoad(false);
    } else {
      toast({
        title: "Upload Image Only",
        description: "Please upload a valid image for Profile",
        status: "warning",
        duration: 5000,
        isClosable: true,
        position: "bottom",
      });
    }
  };

  const submitHandler = () => {};

  return (
    <VStack>
      <FormControl>
        <FormLabel color="white" margin="0">
          Name
        </FormLabel>
        <Input
          onChange={(e) => setName(e.target.value)}
          value={name}
          background="white"
          marginBottom="2"
          placeholder="Enter Name"
          _placeholder={{ opacity: 0.5, color: "blue" }}
        ></Input>
        <FormLabel color="white" margin="0">
          Email
        </FormLabel>
        <Input
          onChange={(e) => setEmail(e.target.value)}
          value={email}
          background="white"
          marginBottom="2"
          placeholder="Enter Email"
          _placeholder={{ opacity: 0.5, color: "blue" }}
        ></Input>
        <FormLabel color="white" margin="0">
          Password{" "}
        </FormLabel>
        <InputGroup size="md">
          <Input
            onChange={(e) => setPass(e.target.value)}
            value={pass}
            pr="4.5rem"
            type={show ? "text" : "password"}
            placeholder="Enter password"
            _placeholder={{ opacity: 0.5, color: "blue" }}
            background="white"
            margin="3px"
          />
          <InputRightElement width="4.5rem">
            <Button
              h="1.75rem"
              size="md"
              onClick={handleClick}
              background="#b3d8f5"
              marginTop="8px"
              marginRight="8px"
            >
              {show ? "Hide" : "Show"}
            </Button>
          </InputRightElement>
        </InputGroup>
        <FormLabel color="white" margin="0">
          Confirm Password{" "}
        </FormLabel>
        <InputGroup size="md">
          <Input
            onChange={(e) => setCpass(e.target.value)}
            value={cpass}
            pr="4.5rem"
            type={show ? "text" : "password"}
            placeholder="Confirm password"
            _placeholder={{ opacity: 0.5, color: "blue" }}
            background="white"
            margin="3px"
          />
          <InputRightElement width="4.5rem">
            <Button
              h="1.75rem"
              size="md"
              onClick={handleClick}
              background="#b3d8f5"
              marginTop="8px"
              marginRight="8px"
            >
              {show ? "Hide" : "Show"}
            </Button>
          </InputRightElement>
        </InputGroup>
        <FormLabel color="white" margin="0">
          {" "}
          Upload Profile{" "}
        </FormLabel>
        <Input
          onChange={(e) => postPic(e.target.file[0])}
          value={pic}
          marginBottom="2"
          color="white"
          type="file"
          border="0"
          margin="3px"
        ></Input>

        <Button
          _hover={{ color: "#2B4EFF", background: "white" }}
          marginLeft="30%"
          w="40%"
          color="white"
          background="#2B4EFF"
          onSubmit={submitHandler}
          isLoading={load}
        >
          {" "}
          SignUp
        </Button>
      </FormControl>
    </VStack>
  );
};

export default Signup;
